    /**
     * @param string $route
     * @return string[]&callable
     * @throws UnresolvableControllerException
     */
    public function resolveControllerRoute(string $route): array {
        if ($this->hasControllerRoute($route) === true) {
            return $this->controllers[$route];
        }

        throw new UnresolvableControllerException(sprintf(
            "Error: Controller '%s' was not found.",
            $route
        ));
    }

    /**
     * @param string $route
     * @return bool
     */
    public function hasControllerRoute(string $route): bool {
        return array_key_exists($route, $this->controllers);
    }

    /**
     * @param string $route
     * @param string[]&callable $callable
     */
    public function addControllerRoute(string $route, array $callable): void {
        $this->controllers[$route] = $callable;
    }
